<div id="add_varient_modal" class="modal fade" role="dialog" data-backdrop="static">    <div class="modal-dialog modal-md">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">                    &times;                </button>                <h4 class="modal-title">Add Variant</h4>            </div>            <form role="form" id="form_varient" method="post">                <div class="modal-body">                    <div id="loader">                        <h1 class="ml8"><span class="letters-container"> <span class="letters letters-left"><img src="<?php echo base_url('assets/'); ?>images/loader-icon.png" width="40px"></span></span><span class="circle circle-white"></span><span class="circle circle-dark"></span><span class="circle circle-container"><span class="circle circle-dark-dashed"></span></span></h1>                    </div>                    <div class="row">                        <div class="col-sm-6">                            <div class="form-group">                                <label for="varient_key">Variant Key<span class="validation-color">*</span></label>                                <div class="input-group">                                    <div class="input-group-addon">                                        <a data-toggle="modal" data-target="#varient_modal" class="pull-right" href="" title="Add Varient" onclick="">+</a>                                    </div>                                    <select class="form-control select2" id="varient_key" name="varient_key">                                        <option value="">Select</option>                                        <?php                                        foreach ($varients as $row) {                                            echo "<option value='$row->varients_id'" . set_select('category', $row->varients_id) . ">$row->varient_key</option>";                                        }                                        ?>                                    </select>                                </div>                                <span class="validation-color" id="err_varient_key"></span>                            </div>                        </div>                        <div class="col-sm-6">                            <div class="form-group">                                <label for="varient_value">Variant Value<span class="validation-color">*</span></label>                                <input type="text" class="form-control" id="varient_value" data-role="tagsinput" name="varient_value[]" value="">                                <span class="validation-color" id="err_varient_value"></span>                            </div>                        </div>                    </div>                </div>                <div class="modal-footer">                    <button type="button" id="varient_submit" class="btn btn-info">                        Add                    </button>                    <button type="button" class="btn btn-info" data-dismiss="modal">                        Cancel                    </button>                </div>            </form>        </div>    </div></div><script type="text/javascript">    $("#varient_submit").click(function () {        var varients = $('#varient_value').val();        if ($('#varient_key').val() == '') {            $('#err_varient_key').text('Please Select Variant Key');            return false;        } else {            $('#err_varient_key').text('');        }        if (varients.length == 0) {            $('#err_varient_value').text('Please Enter Variant Value');            return false;        } else {            $('#err_varient_value').text('');        }        var data = $('#form_varient').serialize();        $.ajax({            url: base_url + 'varients/add_varients_ajax',            dataType: 'JSON',            method: 'POST',            data: data,            beforeSend: function(){                // Show image container                $("#loader").show();            },             success: function (result) {                if (result.flag) {                    $("#loader").hide();                    $('#add_varient_modal').modal('hide');                    dTable.destroy();                    dTable = getAllVarients();                    alert_d.text = result.msg;                    PNotify.success(alert_d);                    $('#form_varient').trigger("reset");                    $('#varient_key').select2();                    $('#varient_value').tagsinput('removeAll');                } else {                    $("#loader").hide();                    $('#add_varient_modal').modal('hide');                    alert_d.text = result.msg;                    PNotify.error(alert_d);                }            }        });    });</script>