<div id="edit_varient_key_modal" class="modal fade" role="dialog" data-backdrop="static">    <div class="modal-dialog">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">                    &times;                </button>                <h4 class="modal-title">Edit Variant Key</h4>            </div>            <form id="edit_varient_key_form">                <div class="modal-body">                    <div id="loader_coco">                        <h1 class="ml8">                            <span class="letters-container">                                <span class="letters letters-left">                                    <img src="<?php echo base_url('assets/'); ?>images/loader-icon.png" width="40px">                                </span>                            </span>                            <span class="circle circle-white"></span>                            <span class="circle circle-dark"></span>                            <span class="circle circle-container">                            <span class="circle circle-dark-dashed"></span>                            </span></h1>                    </div>                    <div class="form-group">                        <label for="varient_name"><!-- Category Name -->Variant Key <span class="validation-color">*</span></label>                        <input type="text" class="form-control" id="varient_key_e" name="varient_key_e">                        <input type="hidden" class="form-control" id="varient_key_id_e" name="varient_key_id_e">                        <span class="validation-color" id="err_varient_key_e"></span>                    </div>                </div>                <div class="modal-footer">                    <button type="submit" id="varient_key_edit_submit" class="btn btn-info" class="close" data-dismiss="modal">                        Update                    </button>                    <button type="button" class="btn btn-info" class="close" data-dismiss="modal">                        Cancel                    </button>                </div>            </form>        </div>    </div></div><script src="<?php echo base_url('assets/js/') ?>icon-loader.js"></script><script type="text/javascript">    $(document).on("click", ".edit_varient_key", function () {        var id = $(this).data('id');        $.ajax({            url: base_url + 'varients/get_varient_key_modal/' + id,            dataType: 'JSON',            method: 'POST',            success: function (result) {                $('#varient_key_e').val(result[0].varient_key);                $('#varient_key_id_e').val(id);            }        });    });    $(document).ready(function () {        $("#varient_key_edit_submit").click(function (event) {            var varient_key_id = $('#varient_key_id_e').val();            var varient_key = $('#varient_key_e').val();            if (varient_key == null || varient_key == "") {                $("#err_varient_key_e").text("Enter Varient Key");                return !1            } else {                $("#err_varient_key_e").text("")            }            $.ajax({                url: base_url + 'varients/edit_varient_key_modal',                dataType: 'JSON',                method: 'POST',                data: {                    'id': varient_key_id,                    'varient_key': varient_key                },                beforeSend: function(){                    // Show image container                    $("#edit_varient_key_modal #loader_coco").show();                },                success: function (result) {                    setTimeout(function () {// wait for 5 secs(2)                            $(document).find('[name=check_item]').trigger('change');                            //location.reload(); // then reload the page.(3)                        },500);                    if(result.flag){                        $('#edit_varient_key_modal').modal('hide');                        $("#edit_varient_key_modal #loader_coco").hide();                        dTable.destroy();                        dTable = getAllVarients();                        alert_d.text = result.msg;                        PNotify.success(alert_d);                    }else{                        $('#edit_varient_key_modal').modal('hide');                        alert_d.text = result.msg;                        PNotify.error(alert_d);                    }                }            });        });    });</script>