art = {        chart: {            plotBackgroundColor: null,            plotBorderWidth: null,            plotShadow: false,            type: 'pie',        },        title: {            text: 'Top Sales'        },        tooltip: {            pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>'        },        plotOptions: {            pie: {                allowPointSelect: true,                cursor: 'pointer',                dataLabels: {                    enabled: true,                    format: '<b>{point.name}</b>: {point.percentage:.1f} % <br> {point.batch}',                    style: {                        color: (Highcharts.theme && Highcharts.theme.contrastTextColor) || '#070586'                    },                    connectorColor: 'silver'                }            }        },        series: [],        "colors": [            "#30a4fb",            "#58bd5c",            "#febf44",            "#ff687e",            "#907bd9",            "#f15c80",            "#e4d354",            "#2b908f",            "#f45b5b",            "#91e8e1"        ]    }    Highcharts.setOptions({        colors: Highcharts.map(Highcharts.getOptions().colors, function (color) {            return {                radialGradient: {                    cx: 0.5,                    cy: 0.3,                    r: 0.7                },                stops: [                    [0, color],                    [1, Highcharts.Color(color).brighten(-0.5).get('rgb')] // darken                ]            };        })    });    function report_sales_graph(json) {        total_line_chart.series = json.sales;        total_line_chart.title.text = 'Total Sales';        total_line_chart.yAxis.title.text = 'Total Sales';        total_line_chart.xAxis.categories = json.time_data;        Highcharts.chart('total_sales_chart', total_line_chart);    }    function report_purchase_graph(json) {        total_line_chart.title.text = 'Total Purchase';        total_line_chart.yAxis.title.text = 'Total Purchase';        total_line_chart.series = json.purchase;        total_line_chart.xAxis.categories = json.time_data;        Highcharts.chart('total_purchase_chart', total_line_chart);    }    function report_expense_graph(json) {        total_line_chart.title.text = 'Total Expense';        total_line_chart.yAxis.title.text = 'Total Expense';        total_line_chart.series = json.expense;        total_line_chart.xAxis.categories = json.time_data;        Highcharts.chart('total_expense_chart', total_line_chart);    }    function report_stock_graph(json) {        total_line_chart.title.text = 'Total Stock';        total_line_chart.yAxis.title.text = 'Total Stock';        total_line_chart.series = json.stocks;        total_line_chart.xAxis.categories = json.time_data;        Highcharts.chart('total_stock_chart', total_line_chart);    }    function report_product_graph(json) {        Product_chart.series[0] = json;        Highcharts.chart('Product_chart_div', Product_chart);    }    function SalesAnalytic(filter, start_date, end_date, first = '0') {        var first = first;        $("#admin_overlay").show();        if (Sxhr && Sxhr.readyState != 4) {            Sxhr.abort();        }        Sxhr = $.ajax({            url: '<?= base_url(); ?>Auth/salesGraph',            type: 'post',            data: {filter: filter, start_date: start_date, end_date: end_date},            dataType: 'json',            /* async:'false',*/            success: function (json) {                $('#sales_data .total').text(json.total_sales);                $('#sales_data .invoice_amt').text(json.total_invoice);                $('#sales_data .pending').text(json.total_pending);                $('#sales_data .received').text(json.total_received);                report_sales_graph(json);            }, complete: function () {                if (first == 1)                    PurchaseAnalytic('last_7_days', '', '', 1);            }        });    }    function PurchaseAnalytic(filter, start_date, end_date, first = '0') {        $("#admin_overlay").show();        if (Pxhr && Pxhr.readyState != 4) {            Pxhr.abort();        }        Pxhr = $.ajax({            url: '<?= base_url(); ?>Auth/purchaseGraph',            type: 'post',            data: {filter: filter, start_date: start_date, end_date: end_date},            dataType: 'json',            /*async:'false',*/            success: function (json) {                $('#purchase_data .total').text(json.total_purchase);                $('#purchase_data .invoice_amt').text(json.total_invoice);                $('#purchase_data .pending').text(json.total_pending);                $('#purchase_data .received').text(json.total_received);                report_purchase_graph(json);            }, complete: function () {                if (first == 1)                    ExpenseAnalytic('last_7_days', '', '', 1);            }        });    }    function ExpenseAnalytic(filter, start_date, end_date, first = '0') {        $("#admin_overlay").show();        if (Exhr && Exhr.readyState != 4) {            Exhr.abort();        }        Exhr = $.ajax({            url: '<?= base_url(); ?>Auth/expenseGraph',            type: 'post',            data: {filter: filter, start_date: start_date, end_date: end_date},            dataType: 'json',            /*async:'false',*/            success: function (json) {                $('#expense_data .total').text(json.total_expense);                $('#expense_data .invoice_amt').text(json.total_invoice);                $('#expense_data .pending').text(json.total_pending);                $('#expense_data .received').text(json.total_received);                report_expense_graph(json);            }, complete: function () {                if (first == 1)                    StockAnalytic('last_7_days', '', '', 1);            }        });    }    function StockAnalytic(filter, start_date, end_date, first = '0') {        $("#admin_overlay").show();        if (Stxhr && Stxhr.readyState != 4) {            Stxhr.abort();        }        Stxhr = $.ajax({            url: '<?= base_url(); ?>Auth/stockGraph',            type: 'post',            data: {filter: filter, start_date: start_date, end_date: end_date},            dataType: 'json',            /*async:'false',*/            success: function (json) {                $('#stock_data .total').text(json.total_products);                $('#stock_data .invoice_amt').text(json.total_stock);                $('#stock_data .pending').text(json.total_damaged);                $('#stock_data .received').text(json.total_missing);                report_stock_graph(json);            }, complete: function () {                if (first == 1)                    ProductAnalytic('last_7_days', '', '');            }        });    }    function ProductAnalytic(filter, start_date, end_date) {        $("#admin_overlay").show();        if (pro_xhr && pro_xhr.readyState != 4) {            pro_xhr.abort();        }        pro_xhr = $.ajax({            url: '<?= base_url(); ?>Auth/productChart',            type: 'post',            data: {filter: filter, start_date: start_date, end_date: end_date},            dataType: 'json',            /*async:'false',*/            success: function (json) {                /*$(document).find('.loader').hide();                 $(document).find('.loader').parent().removeClass('overlay');                 $('.share_tab').attr('disabled',false);*/                report_product_graph(json);            }        });    }    $(document).ready(function () {        SalesAnalytic('last_7_days', '', '', 1);        $('#sales_filter').on('change', function () {            $('#total_sales_chart').html('');            $('#sales_data .h2_num').text('0');            SalesAnalytic($(this).val());        });        $('#purchase_filter').on('change', function () {            $('#total_purchase_chart').html('');            $('#purchase_data .h2_num').text('0');            PurchaseAnalytic($(this).val());        });        $('#expense_filter').on('change', function () {            $('#total_expense_chart').html('');            $('#expense_data .h2_num').text('0');            ExpenseAnalytic($(this).val());        });        $('#stock_filter').on('change', function () {            $('#total_stock_chart').html('');            $('#stock_data .h2_num').text('0');            StockAnalytic($(this).val());        });    });</script><?php if($this->session->flashdata('welcomeLoader') == 1){ ?><script type="text/javascript">    setTimeout(function(){        $('.composition-wrapper').hide();        $('body').css('position', 'relative');    },8000); </script><?php } ?>