<div id="edit_hsn_modal" class="modal fade z_index_modal" role="dialog" data-backdrop="static">    <div class="modal-dialog modal-md">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">                    &times;                </button>                <h4 class="modal-title">Edit HSN</h4>            </div>            <div class="modal-body">                <div id="loader_coco">                        <h1 class="ml8">                            <span class="letters-container">                                <span class="letters letters-left">                                    <img src="<?php echo base_url('assets/'); ?>images/loader-icon.png" width="40px">                                </span>                            </span>                            <span class="circle circle-white"></span>                            <span class="circle circle-dark"></span>                            <span class="circle circle-container">                            <span class="circle circle-dark-dashed"></span>                            </span></h1>                    </div>                <div class="row">                    <div class="col-sm-6">                        <div class="form-group">                            <input type="hidden" name="hsn_id" id="hsn_id">                            <label for="hsnType_edit">HSN Type<span class="validation-color">*</span></label>                            <select class="form-control select2" id="hsnType_edit" name="hsnType_edit" style="width: 100%">                                <option value="">Select HSN Type</option>                                <option value="goods">Goods/Product</option>                                <option value="services">Services</option>                            </select>                            <span class="validation-color" id="err_hsnType_edit"><?php echo form_error('err_hsnType_edit'); ?></span>                        </div>                    </div>                    <div class="col-sm-6">                        <div class="form-group">                            <label for="hsnCode_edit">HSN Code<span class="validation-color">*</span></label>                            <input type="text" class="form-control" id="hsnCode_edit" name="hsnCode_edit">                            <span class="validation-color" id="err_hsn_code_edit"><?php echo form_error('err_hsn_code_edit'); ?></span>                        </div>                    </div>                    <div class="col-sm-12">                        <div class="form-group">                            <label for="description">Description</label>                            <textarea class="form-control" id="description_edit" name="description_edit" ></textarea>								                        </div>                    </div>                </div>            </div>            <div class="modal-footer">                <button type="submit" id="updateHsn" class="btn btn-info">                    Update                </button>                <button type="button" class="btn btn-info" data-dismiss="modal">                    Cancel                </button>            </div>        </div>    </div></div><script src="<?php echo base_url('assets/js/') ?>icon-loader.js"></script><script type="text/javascript">    $(document).on("click", ".edit_hsn", function () {        var id = $(this).data('id');        $.ajax({            url: base_url + 'hsn/get_hsn_modal/' + id,            dataType: 'JSON',            method: 'POST',            success: function (result) {                $('#hsn_id').val(result[0].hsn_id);                $('#hsnCode_edit').val(result[0].hsn_code);                $('#description_edit').val(result[0].description);                $('#hsnType_edit').val(result[0].type).change();            }        });    });    $(document).ready(function () {        $("#updateHsn").click(function (event) {            var hsn_code = $('#hsnCode_edit').val();            var hsnType_edit = $('#hsnType_edit').val();            var description = $('#description_edit').val();            var hsn_id = $('#hsn_id').val();            // var num_regex="^\$?([0-9]{1,3},([0-9]{3},)*[0-9]{3}|[0-9]+)(.[0-9][0-9])?$";            var hsn_regex = /^\d[0-9.,\s]+$/;            if (hsnType_edit == null || hsnType_edit == "") {                $("#err_hsnType_edit").text("Please Select HSN Type.");                $('#err_hsnType_edit').focus();                return !1            } else {                $("#err_hsnType_edit").text("")            }            if (hsn_code == null || hsn_code == "") {                $("#err_hsn_code_edit").text("Please Enter HSN Code.");                $('#err_hsn_code_edit').focus();                return !1            } else {                $("#err_hsn_code_edit").text("")            }            if (!hsn_code.match(hsn_regex)) {                $("#err_hsn_code_edit").text("Please Enter Valid HSN Code ");                return !1;            } else {                $("#err_hsn_code_edit").text("");            }            $.ajax({                url: base_url + 'hsn/update_hsn_modal',                dataType: 'JSON',                method: 'POST',                data: {'hsn_code': hsn_code, 'hsnType_edit': hsnType_edit, 'description': description, 'id': hsn_id},                beforeSend: function(){                     // Show image container                    $("#loader_coco").show();                },                 success: function (result) {                    if(result.flag){                        $('#loader_coco').hide();                        $('#edit_hsn_modal').modal('hide');                        dTable.destroy();                        dTable = getAllHsn();                        alert_d.text = result.msg;                        PNotify.success(alert_d);                        setTimeout(function () {// wait for 5 secs(2)                            $(document).find('[name=check_item]').trigger('change');                            //location.reload(); // then reload the page.(3)                        },500);                    }else{                        if(result.msg == 'duplicate'){                            $('#err_hsn_code_edit').text('The Hsn code is already exist.');                            return false;                        }else{                            $('#edit_hsn_modal').modal('hide');                            alert_d.text = result.msg;                            PNotify.error(alert_d);                            setTimeout(function () {// wait for 5 secs(2)                                $(document).find('[name=check_item]').trigger('change');                                //location.reload(); // then reload the page.(3)                            },500);                        }                    }                },                error: function(msg)                {                    $('#loader_coco').hide();                    alert_d.text ='Something Went Wrong!';                    PNotify.error(alert_d);                }            });        });        $('.select2').select2({            minimumResultsForSearch: -1        });    });</script>