<div class="modal fade" id="update_privillege" role="dialog" data-backdrop="static">    <div class="modal-dialog">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 class="modal-title">Update Privileges</h4>            </div>            <!-- <form role="form" id="form" method="post" action="<?php echo base_url('receipt/pay_now'); ?>"> -->            <div class="modal-body">                <input type="hidden" name="accesbility_id" id="accesbility_id">                <div class="row">                    <div class="col-md-2">                        <div class="form-group">                            <label>Add</label>                            <input class="add report_privilege privilege_static" type="checkbox" checked="" id="add" name="add">                        </div>                    </div>                    <div class="col-md-2">                        <div class="form-group">                            <label>View</label>                            <input class="view privilege_static" type="checkbox" id="view" name="view">                        </div>                    </div>                    <div class="col-md-2">                        <div class="form-group">                            <label>Edit</label>                            <input class="edit report_privilege privilege_static" type="checkbox" id="edit" name="edit">                        </div>                    </div>                    <div class="col-md-2">                        <div class="form-group">                            <label>Delete</label>                            <input class="delete report_privilege privilege_static" type="checkbox" id="delete" name="delete">                        </div>                    </div>                </div>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-primary" id="privillege_button">Submit</button>                <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>            </div>        </div>    </div></div><script type = "text/javascript" >    $(document).on("click", '.privilege', function (e) {        var acc_id = $(this).data('id');        $("#accesbility_id").val(acc_id);        $.ajax({            url: base_url + 'privilege/get_privilege/' + acc_id,            dataType: 'JSON',            method: 'POST',            success: function (result) {                /*console.log(result);*/                $('.report_privilege').attr('disabled',false);                if(result[0].is_report == 1){                    $('.report_privilege').attr('disabled',true);                }                if(result[0].module_id == 37){                    $('.privilege_static').attr('disabled',true);                }                // console.log(result[0].add_privilege) ;                if (result[0].add_privilege == "yes") {                    $(".add").prop("checked", true);                } else {                    $(".add").prop("checked", false);                }                if (result[0].view_privilege == "yes") {                    $(".view").prop("checked", true);                } else {                    $(".view").prop("checked", false);                }                if (result[0].edit_privilege == "yes") {                    $(".edit").prop("checked", true);                } else {                    $(".edit").prop("checked", false);                }                if (result[0].delete_privilege == "yes") {                    $(".delete").prop("checked", true);                } else {                    $(".delete").prop("checked", false);                }            }        });    });    $("#privillege_button").click(function (event) {        if ($(".add").prop('checked') == true) {            var add = "yes";        } else {            var add = "no";        }        if ($(".edit").prop('checked') == true) {            var edit = "yes";        } else {            var edit = "no";        }        if ($(".view").prop('checked') == true) {            var view = "yes";        } else {            var view = "no";        }        if ($(".delete").prop('checked') == true) {            var delet = "yes";        } else {            var delet = "no";        }        $.ajax({            url: base_url + 'privilege/update_privilege',            dataType: 'JSON',            method: 'POST',            data: {                'acc_id': $('#accesbility_id').val(),                'add_privilege': add,                'edit_privilege': edit,                'delete_privilege': delet,                'view_privilege': view            },            success: function (result) {                setTimeout(function () { // wait for 5 secs(2)                    location.reload(); // then reload the page.(3)                });            }        });    });</script>