     }            $.ajax({                url: base_url + 'superadmin/modules/post_user_access',                type: "POST",                data:                        {                            'user_id': user_id,                            'batch_id': batchId,                            'add': add,                            'view': view,                            'edit': edit,                            'del': del,                            'module_id': module_id,                            'module_name': module_name                        },                success: function (result) {                    //console.log(result);                    location.reload();                }            });        });    });</script><script type="text/javascript">    $(document).on("click", ".delete_record", function () {        var id = $(this).data('id');        var r = confirm("Are u sure?");        if (r == true) {            $.ajax({                url: base_url + 'superadmin/modules/privilege_delete/' + id,                type: "GET",                success: function (result) {                    // alert(result)                    location.reload();                    //   if(result == "sucess"){                    // $("#row"+id).hide();                    //   }                }            });        }    });</script>